        address to,
        address asset,
        uint amount
    ) external virtual;

    function approveThis(
        address manager,
        address asset,
        uint amount
    ) external virtual;

    function withdrawReserves(address to, uint amount) external virtual;

    function absorb(
        address absorber,
        address[] calldata accounts
    ) external virtual;

    function buyCollateral(
        address asset,
        uint minAmount,
        uint baseAmount,
        address recipient
    ) external virtual;

    function quoteCollateral(
        address asset,
        uint baseAmount
    ) public view virtual returns (uint);

    function getAssetInfo(
